import React from "react";
import Game, {Token, TokenToColor} from "./Game";
import {ROWS, COLS} from "./Constants";
import './Board.css'

interface Props {
}

interface State {
    board: Token[][]
}

class Board extends React.Component<Props, State> {

    private game: Game;

    constructor(props: Props) {
        super(props);

        this.game = new Game()

        this.state = {
            board: this.game.board
        }
    }

    transpose(): Token[][] {
        let transpose: Token[][] = []
        for (let c = 0; c < COLS; c++) {
            transpose.push([])
            for (let r = 0; r < ROWS; r++) {
                transpose[c].push(this.state.board[r][c])
            }
        }
        return transpose
    }

    won() {
        alert(`${this.game.playerName} won!`)
    }

    render() {
        return (<div>
            <h1>Connect4 AI</h1>
            <div className="Row">
                <p>Current Player: </p>
                <div className="CurrentPlayer" style={{backgroundColor: TokenToColor(this.game.token)}}/>
            </div>
            <div className='Board'>
                {this.transpose().map((col, index) => (
                    <div className='Column' onClick={() => {
                        let won = this.game.play(index)
                        this.setState({board: this.game.board})
                        if (won) {
                            this.won()
                        }
                    }}>
                        {col.map(tok => (<TokenView tok={tok}/>))}
                    </div>
                ))}
            </div>
        </div>)
    }
}

interface TokenProps {
    tok: Token,
}

interface TokenState {
}

class TokenView extends React.Component<TokenProps, TokenState> {

    render() {
        return <div className='Token'
                    style={{backgroundColor: TokenToColor(this.props.tok)}}/>
    }

}

export default Board;
