import {COLS, ROWS} from "./Constants";
import '../Extensions'

export enum Token {
    red, yellow, none
}

export enum Player {
    human, ai
}

export function TokenToColor(tok: Token) {
    switch (tok) {
        case Token.none:
            return '#fff'
        case Token.red:
            return '#ff5252'
        case Token.yellow:
            return '#ffea00'
    }
}

export function PlayerToToken(player: Player) {
    switch (player) {
        case Player.ai:
            return Token.red
        case Player.human:
            return Token.yellow
    }
}

class Game {

    public board: Token[][]
    public currentPlayer: Player

    private heights: number[]

    constructor() {
        let board: Token[][] = []
        for (let r = 0; r < ROWS; r++) {
            board.push([])
            for (let c = 0; c < COLS; c++) {
                board[r].push(Token.none)
            }
        }

        this.board = board
        this.currentPlayer = Player.human
        this.heights = Array.repeat(0, COLS)
    }

    private get token(): Token {
        return PlayerToToken(this.currentPlayer)
    }

    private togglePlayer() {
        if (this.currentPlayer === Player.human) {
            this.currentPlayer = Player.ai
        } else {
            this.currentPlayer = Player.human
        }
    }

    public play(col: number): boolean {
        if (col >= COLS) {
            throw new Error("invalid column number")
        } else if (this.heights[col] >= COLS) {
            return false;
        }

        // set token
        this.board[ROWS - this.heights[col] - 1][col] = this.token

        // add to colHeights
        this.heights[col]++;

        // switch player
        this.togglePlayer()

        return true;
    }

}

export default Game
