import React from "react";
import Game, {Token, TokenToColor} from "./Game";
import {ROWS, COLS} from "./Constants";
import './Board.css'

interface Props {
}

interface State {
    board: Token[][]
    game: Game
}

class Board extends React.Component<Props, State> {

    constructor(props: Props) {
        super(props);

        let board: Token[][] = []
        for (let r = 0; r < ROWS; r++) {
            board.push([])
            for (let c = 0; c < COLS; c++) {
                board[r].push(Token.none)
            }
        }

        this.state = {
            board: board,
            game: new Game()
        }
    }

    transpose(): Token[][] {
        let transpose: Token[][] = []
        for (let c = 0; c < COLS; c++) {
            transpose.push([])
            for (let r = 0; r < ROWS; r++) {
                transpose[c].push(this.state.board[r][c])
            }
        }
        return transpose
    }

    render() {
        return (<div>
            <h1>Connect4 AI</h1>
            <div className='Board'>
                {this.state.board.map((row: Token[]) => (
                    <div className='Column'>
                        {row.map(tok => (<TokenView tok={tok}/>))}
                    </div>
                ))}
            </div>
        </div>)
    }
}

interface TokenProps {
    tok: Token
}

interface TokenState {
}

class TokenView extends React.Component<TokenProps, TokenState> {

    render() {
        return <div className='Token'
                    style={{backgroundColor: TokenToColor(this.props.tok)}}/>
    }

}

export default Board;
