import React from "react";
import {Token} from "./Game";
import {Constants} from "./Constants";

interface Props {
}

interface State {
    board: Token[][]
}

class Board extends React.Component<Props, State> {

    constructor(props: Props) {
        super(props);

        let board: Token[][] = []
        for (let r = 0; r < Constants.ROWS; r++) {
            board.push([])
            for (let c = 0; c < Constants.COLS; c++)  {
                board[r].push(Token.none)
            }
        }

        this.state = {
            board: board
        }
    }

    render() {
        return <div>
            {this.state.board.map((row: Token[]) => (
                <div>
                    {row.map(tok => (<TokenView tok={tok}/>))}
                </div>
            ))}
        </div>
    }
}

interface TokenProps {
    tok: Token
}

interface TokenState {
}

class TokenView extends React.Component<TokenProps, TokenState> {

    render() {
        return <div style={{borderRadius: 50, backgroundColor: 'white', width: 50, height: 50}}/>
    }

}

export default Board;
