import {COLS, ROWS, WIN} from "./Constants";
import '../Extensions'
import {Simulate} from "react-dom/test-utils";

export enum Token {
    red, yellow, none
}

export enum Player {
    human, ai
}

export function TokenToColor(tok: Token) {
    switch (tok) {
        case Token.none:
            return '#fff'
        case Token.red:
            return '#ff5252'
        case Token.yellow:
            return '#ffea00'
    }
}

export function PlayerToToken(player: Player) {
    switch (player) {
        case Player.ai:
            return Token.red
        case Player.human:
            return Token.yellow
    }
}

export function PlayerToText(player: Player) {
    switch (player) {
        case Player.ai:
            return "AI"
        case Player.human:
            return "Human"
    }
}

class Game {

    public board: Token[][]
    public currentPlayer: Player

    private heights: number[]

    constructor() {
        let board: Token[][] = []
        for (let r = 0; r < ROWS; r++) {
            board.push([])
            for (let c = 0; c < COLS; c++) {
                board[r].push(Token.none)
            }
        }

        this.board = board
        this.currentPlayer = Player.human
        this.heights = Array.repeat(0, COLS)
    }

    public get token(): Token {
        return PlayerToToken(this.currentPlayer)
    }

    private togglePlayer() {
        if (this.currentPlayer === Player.human) {
            this.currentPlayer = Player.ai
        } else {
            this.currentPlayer = Player.human
        }
    }

    /**
     * plays selected column with current player
     * @param col column to choose
     * @return returns if the current player won
     */
    public play(col: number): boolean {
        if (col >= COLS) {
            throw new Error("invalid column number")
        } else if (this.heights[col] >= COLS - 1) {
            return false;
        }

        // set token
        this.board[ROWS - this.heights[col] - 1][col] = this.token

        // add to colHeights
        this.heights[col]++;

        let won = this.checkWinner()

        // switch player
        if (!won) this.togglePlayer()

        return won;
    }

    public checkWinner(): boolean {
        let tok = this.token

        for (let r = 0; r < ROWS; r++) {
            for (let c = 0; c < COLS; c++) {
                if ((r > ROWS - WIN && c > COLS - WIN) || this.board[r][c] !== tok) {
                    // no need to check through positions
                    break;
                }
                let ct = 1;

                // check right
                for (let i = 1; i < WIN; i++) {
                    if (this.board[r][c + i] !== tok) {
                        break;
                    }
                    ct++;
                }

                if (ct === WIN) {
                    return true;
                }

                // check down
                ct = 1;
                for (let i = 1; i < WIN; i++) {
                    if (this.board[r + i][c] !== tok) {
                        break;
                    }
                    ct++;
                }

                if (ct === WIN) {
                    return true;
                }

                // check r-d diagonal
                ct = 1;
                for (let i = 1; i < WIN; i++) {
                    if (this.board[r + i][c + i] !== tok) {
                        break;
                    }
                    ct++;
                }

                if (ct === WIN) {
                    return true;
                }

                // check r-u diagonal
                if (r >= WIN - 1) {
                    ct = 1;
                    for (let i = 1; i < WIN; i++) {
                        if (this.board[r - i][c + i] !== tok) {
                            break;
                        }
                        ct++;
                    }

                    if (ct === WIN) {
                        return true;
                    }
                }
            }
        }
        return false;
    }

}

export default Game
