import React from "react";
import Game, {Token, TokenToColor} from "./Game";
import {ROWS, COLS} from "./Constants";
import './Board.css'

interface Props {
}

interface State {
    game: Game
}

class Board extends React.Component<Props, State> {

    constructor(props: Props) {
        super(props);

        this.state = {
            game: new Game()
        }
    }

    transpose(): Token[][] {
        let transpose: Token[][] = []
        for (let c = 0; c < COLS; c++) {
            transpose.push([])
            for (let r = 0; r < ROWS; r++) {
                transpose[c].push(this.state.game.board[r][c])
            }
        }
        return transpose
    }

    render() {
        return (<div>
            <h1>Connect4 AI</h1>
            <div className='Board'>
                {this.state.game.board.map((col, index) => (
                    <div className='Column'>
                        {col.map(tok => (<TokenView col={index} tok={tok}/>))}
                    </div>
                ))}
            </div>
        </div>)
    }
}

interface TokenProps {
    tok: Token,
    col: number
}

interface TokenState {
}

class TokenView extends React.Component<TokenProps, TokenState> {

    render() {
        return <div className='Token'
                    onClick={() => {
                        alert("hello")
                    }
                    }
                    style={{backgroundColor: TokenToColor(this.props.tok)}}/>
    }

}

export default Board;
