import {COLS, ROWS} from "./Constants";

Array.prototype.repeat = function(what, L) {

}

export enum Token {
    red, yellow, none
}

export enum Player {
    human, ai
}

export function TokenToColor(tok: Token) {
    switch (tok) {
        case Token.none:
            return '#fff'
        case Token.red:
            return '#ff5252'
        case Token.yellow:
            return '#ffea00'
    }
}

class Game {

    public board: Token[][]
    public currentPlayer: Player

    private colHeights: number[]

    constructor() {
        let board: Token[][] = []
        for (let r = 0; r < ROWS; r++) {
            board.push([])
            for (let c = 0; c < COLS; c++) {
                board[r].push(Token.none)
            }
        }

        this.board = board
        this.currentPlayer = Player.human
        this.colHeights = new Array()
    }

    public play(col: number) {
        if (col >= COLS) {
            throw new Error("invalid column number")
        }

    }

}

export default Game
